INSERT INTO faq (id, q, a) VALUES
(gen_random_uuid(), '¿Dónde se encuentra la información sobre la gestión administrativa de la Cámara de Diputados?', 'La información administrativa se encuentra en la intranet institucional y en la sección de transparencia del sitio web oficial.'),
(gen_random_uuid(), '¿Quién es la Máxima Autoridad Ejecutiva (MAE) de la Cámara de Diputados?', 'La MAE es la principal autoridad responsable de la gestión administrativa y financiera de la institución.'),
(gen_random_uuid(), '¿Cuál es el propósito del Reglamento Interno de Personal?', 'El reglamento establece las normas, derechos, obligaciones y prohibiciones que rigen la relación laboral entre la Cámara de Diputados y sus funcionarios.'),
(gen_random_uuid(), '¿Cómo se accede a la intranet institucional?', 'El acceso a la intranet es exclusivo para funcionarios y se realiza a través de credenciales de usuario y contraseña proporcionadas por la unidad de informática.'),
(gen_random_uuid(), '¿Qué se puede encontrar en el Módulo de Activos Fijos del sistema CADI?', 'En este módulo se puede consultar el inventario de activos, el estado de los bienes y realizar solicitudes de mantenimiento o baja.'),
(gen_random_uuid(), '¿Qué tipo de reportes se pueden generar en el sistema CADI?', 'El sistema CADI permite generar reportes de inventario, de correspondencia, de ejecución del POA y otros informes administrativos.'),
(gen_random_uuid(), '¿Qué es el subsistema de evaluación del desempeño?', 'Es un proceso permanente para medir el grado de cumplimiento de los objetivos y funciones asignadas a un puesto de trabajo.'),
(gen_random_uuid(), '¿Cuál es el objetivo principal de la evaluación del desempeño?', 'Su objetivo es mejorar la eficiencia de la entidad, detectar necesidades de capacitación y proveer información para ascensos y promociones.'),
(gen_random_uuid(), '¿Qué sucede si el resultado de la evaluación del desempeño no es satisfactorio?', 'Un desempeño no satisfactorio puede llevar a la toma de medidas correctivas, que pueden incluir la separación del servidor público de la institución.'),
(gen_random_uuid(), '¿Quiénes son los responsables de evaluar el desempeño?', 'La evaluación de desempeño es responsabilidad del superior inmediato de cada funcionario, en coordinación con la Dirección de Recursos Humanos.'),
(gen_random_uuid(), '¿Qué debo hacer si tengo un problema con mi sistema de registro de asistencia?', 'Debes reportar cualquier problema con el sistema de asistencia a la Dirección de Recursos Humanos o a la unidad de informática para que sea solucionado.'),
(gen_random_uuid(), '¿Qué debo hacer si necesito un permiso por razones de fuerza mayor?', 'Los permisos por razones de fuerza mayor deben ser justificados con la documentación correspondiente y aprobados por la autoridad superior.'),
(gen_random_uuid(), '¿Qué son los pasajes y viáticos?', 'Son los recursos asignados a los servidores públicos para cubrir los gastos de transporte, hospedaje y alimentación durante una misión oficial fuera de su lugar de trabajo habitual.'),
(gen_random_uuid(), '¿Se requiere autorización para una comisión oficial al interior del país?', 'Sí, toda comisión oficial requiere la autorización de la Máxima Autoridad Ejecutiva mediante una resolución expresa.'),
(gen_random_uuid(), '¿Qué documentos se deben presentar para el descargo de viáticos?', 'Se deben presentar el informe de la misión, facturas o recibos, pasajes, boletos de abordaje y cualquier otra documentación de respaldo.'),
(gen_random_uuid(), '¿Qué es el Plan Anual de Contrataciones (PAC)?', 'Es un documento de gestión que la entidad elabora anualmente para programar todas las contrataciones de bienes, obras, servicios generales y de consultoría que prevé realizar.'),
(gen_random_uuid(), '¿Cómo se accede a la información sobre el presupuesto?', 'La información sobre el presupuesto y su ejecución se encuentra publicada en el sitio web de la Cámara de Diputados para su libre acceso y consulta.'),
(gen_random_uuid(), '¿Qué se considera una falta grave?', 'Una falta grave es una acción u omisión que contraviene la normativa interna y puede ser sancionada con una suspensión temporal sin goce de haberes.'),
(gen_random_uuid(), '¿Cómo se resuelven los conflictos laborales?', 'Los conflictos laborales se resuelven conforme a lo establecido en la normativa de la Ley General del Trabajo y el Estatuto del Funcionario Público, en coordinación con la Dirección de Recursos Humanos.'),
(gen_random_uuid(), '¿Hay una política de teletrabajo para los funcionarios?', 'La aplicación del teletrabajo se rige por la normativa nacional, incluyendo decretos supremos y resoluciones ministeriales, y puede aplicarse a puestos específicos.'),
(gen_random_uuid(), '¿Qué debo hacer para solicitar teletrabajo?', 'La solicitud de teletrabajo debe realizarse a través de la Dirección de Recursos Humanos, siguiendo los procedimientos establecidos por la institución.'),
(gen_random_uuid(), '¿Quiénes pueden solicitar teletrabajo?', 'La aplicación del teletrabajo puede darse de forma preferencial para personas en situación de vulnerabilidad o mayores de 65 años, entre otros casos debidamente justificados.'),
(gen_random_uuid(), '¿Cómo se gestiona la correspondencia externa que llega a la Cámara de Diputados?', 'La correspondencia externa es registrada y gestionada a través del Módulo de Correspondencia del sistema CADI para su distribución a las unidades correspondientes.'),
(gen_random_uuid(), '¿Qué es el sistema de gestión documental?', 'Es el sistema que permite la organización, almacenamiento y control de los documentos físicos y digitales de la institución.'),
(gen_random_uuid(), '¿Qué es el Archivo Histórico de la Asamblea Legislativa Plurinacional?', 'Es el repositorio oficial de toda la documentación del Órgano Legislativo y la Vicepresidencia del Estado, y resguarda la memoria institucional.'),
(gen_random_uuid(), '¿Puedo acceder a la biblioteca de la Cámara de Diputados?', 'Sí, la biblioteca está abierta para la consulta de investigadores, estudiantes y público en general, y cuenta con un vasto acervo bibliográfico.'),
(gen_random_uuid(), '¿Qué son los reglamentos internos?', 'Son los documentos normativos que establecen los procedimientos específicos y las reglas de conducta para los servidores públicos de la institución.'),
(gen_random_uuid(), '¿Qué es el boletín parlamentario?', 'Es una publicación que contiene información sobre las actividades, iniciativas legislativas y gestiones de los asambleístas de la Cámara de Diputados.'),
(gen_random_uuid(), '¿Cómo se pueden realizar propuestas o sugerencias para mejorar la gestión?', 'Se pueden canalizar propuestas y sugerencias a través de las vías de comunicación interna o a la unidad de calidad y gestión institucional.'),
(gen_random_uuid(), '¿Existe un procedimiento para la reincorporación de un funcionario?', 'Sí, la reincorporación de un funcionario se rige por la normativa del Estatuto del Funcionario Público y la regulación específica de la institución.'),
(gen_random_uuid(), '¿Qué es un certificado de aportes laborales?', 'Es un documento que certifica el tiempo de servicio y los aportes que un funcionario ha realizado al sistema de seguridad social a largo plazo.'),
(gen_random_uuid(), '¿Dónde puedo obtener un certificado de aportes laborales?', 'Debes solicitar este certificado en la Dirección de Recursos Humanos, presentando el formulario correspondiente y tu documentación de identidad.'),
(gen_random_uuid(), '¿Qué son las asignaciones familiares?', 'Son los beneficios sociales que se pagan a los funcionarios por el nacimiento de hijos o por el matrimonio, conforme a lo establecido en la Ley General del Trabajo.'),
(gen_random_uuid(), '¿Qué es el reglamento de ética?', 'Es la normativa que establece los principios éticos y las responsabilidades que deben regir el accionar de los servidores públicos de la Cámara de Diputados.'),
(gen_random_uuid(), '¿Qué es la ética pública?', 'Es el conjunto de valores, principios y normas que guían la conducta de los servidores públicos para asegurar la integridad, la transparencia y el servicio al bien común.'),
(gen_random_uuid(), '¿Qué es el control social?', 'Es la participación de la ciudadanía en el seguimiento, evaluación y fiscalización de la gestión pública para asegurar la transparencia y la rendición de cuentas.'),
(gen_random_uuid(), '¿Cuáles son los canales de comunicación interna para los funcionarios?', 'Los canales de comunicación interna incluyen el correo electrónico institucional, la intranet y comunicados oficiales de las autoridades.'),
(gen_random_uuid(), '¿Qué es una baja por maternidad?', 'Es la licencia con goce de haberes que se concede a las funcionarias embarazadas antes y después del parto, conforme a lo establecido en la normativa de seguridad social.'),
(gen_random_uuid(), '¿Cómo se tramita la baja por maternidad?', 'El trámite se realiza en la Dirección de Recursos Humanos, presentando la certificación médica y demás documentación requerida por la Caja Nacional de Salud.'),
(gen_random_uuid(), '¿Se puede solicitar un anticipo de sueldo?', 'Sí, la normativa interna de la institución puede contemplar la posibilidad de solicitar anticipos de sueldo bajo condiciones específicas y con autorización de la Dirección Administrativa y Financiera.'),
(gen_random_uuid(), '¿Qué son los pasajes aéreos y terrestres?', 'Son los documentos de viaje que se emiten para los servidores públicos que se desplazan en comisión oficial al interior o exterior del país.'),
(gen_random_uuid(), '¿Cómo se gestiona el pedido de pasajes?', 'El pedido de pasajes debe ser autorizado por la Máxima Autoridad Ejecutiva, y el trámite se realiza a través de la unidad de Pasajes y Viáticos.'),
(gen_random_uuid(), '¿Qué es el control de activos fijos?', 'Es el proceso que se encarga de la identificación, registro, inventariación, valuación y baja de los activos fijos de la institución.'),
(gen_random_uuid(), '¿Quién es el responsable del control de activos fijos?', 'El control de activos fijos es responsabilidad de la Unidad de Activos Fijos y de cada funcionario que tiene un bien asignado bajo su custodia.'),
(gen_random_uuid(), '¿Qué es el sistema de gestión de bienes y servicios?', 'Es un sistema que integra los procesos de planificación, adquisición, almacenamiento, distribución y disposición de los bienes y servicios de la institución.'),
(gen_random_uuid(), '¿Dónde puedo encontrar información sobre el personal de la Cámara de Diputados?', 'La información sobre el personal se encuentra en la Dirección de Recursos Humanos y en la intranet institucional, en las secciones correspondientes.'),
(gen_random_uuid(), '¿Qué son los informes de gestión?', 'Son los documentos que las unidades organizacionales y los funcionarios presentan periódicamente para dar cuenta de los resultados y actividades realizadas en un periodo determinado.'),
(gen_random_uuid(), '¿A quién se debe presentar el informe de gestión?', 'Los informes de gestión se presentan al superior inmediato para su revisión y aprobación, y luego a las instancias correspondientes de planificación y control.'),
(gen_random_uuid(), '¿Qué es el sistema de programación de operaciones anual (POA)?', 'El POA es un instrumento de gestión que define los objetivos, resultados y actividades de cada unidad organizacional para la gestión correspondiente.'),
(gen_random_uuid(), '¿Qué son las auditorías internas?', 'Las auditorías internas son evaluaciones periódicas que se realizan para verificar el cumplimiento de los procedimientos administrativos y la normativa interna.'),
(gen_random_uuid(), '¿Quién realiza las auditorías internas?', 'Las auditorías internas son realizadas por la Unidad de Auditoría Interna de la Cámara de Diputados, de manera independiente y objetiva.'),
(gen_random_uuid(), '¿Qué debo hacer si soy objeto de una auditoría?', 'Debes colaborar con la auditoría, proporcionando toda la información y documentación requerida por los auditores para la correcta realización del proceso.'),
(gen_random_uuid(), '¿Qué es el plan estratégico institucional?', 'Es el documento que define la visión, misión, objetivos y metas de la Cámara de Diputados a largo plazo, y que orienta todas las acciones de la institución.'),
(gen_random_uuid(), '¿Cómo se relaciona la POA con el plan estratégico?', 'La POA es la herramienta que operacionaliza el plan estratégico, traduciendo los objetivos de largo plazo en actividades concretas y medibles para la gestión anual.'),
(gen_random_uuid(), '¿Qué tipo de información se considera confidencial?', 'Se considera información confidencial aquella que está protegida por ley, como datos personales, información de seguridad o información clasificada.'),
(gen_random_uuid(), '¿Qué es el uso de vehículos oficiales?', 'Es el uso de los vehículos institucionales para el cumplimiento de misiones oficiales, debidamente autorizado y registrado por la unidad de transportes.'),
(gen_random_uuid(), '¿Qué debo hacer para solicitar un vehículo oficial?', 'La solicitud de un vehículo oficial debe realizarse a través del formulario correspondiente, especificando la misión, el itinerario y la justificación del viaje.'),
(gen_random_uuid(), '¿Qué sucede si un funcionario utiliza un vehículo oficial para uso personal?', 'El uso de vehículos oficiales para fines personales está prohibido y puede dar lugar a sanciones disciplinarias, conforme a la normativa interna.'),
(gen_random_uuid(), '¿Qué es el Comité de Licitaciones?', 'Es la instancia responsable de evaluar las propuestas presentadas en un proceso de licitación para la adquisición de bienes, obras o servicios.'),
(gen_random_uuid(), '¿Qué es la certificación presupuestaria?', 'Es el documento que certifica la disponibilidad de fondos en el presupuesto de la institución para la realización de una actividad, un proyecto o una contratación.'),
(gen_random_uuid(), '¿Quién emite la certificación presupuestaria?', 'La certificación presupuestaria es emitida por la Unidad de Presupuesto o la Dirección Administrativa y Financiera, una vez que se ha verificado la existencia de fondos.'),
(gen_random_uuid(), '¿Qué es el presupuesto institucional?', 'Es el documento financiero que planifica y asigna los recursos económicos a las diferentes unidades organizacionales para que puedan cumplir con sus objetivos.'),
(gen_random_uuid(), '¿Qué es un activo fijo tangible?', 'Es un bien físico, como un edificio, un vehículo o un equipo de computación, que la institución posee para el desarrollo de sus actividades.'),
(gen_random_uuid(), '¿Qué es un activo fijo intangible?', 'Es un bien no físico, como un software o una licencia, que la institución posee para el desarrollo de sus actividades.'),
(gen_random_uuid(), '¿Qué es un bien de consumo?', 'Es un bien que se utiliza una sola vez o que tiene una vida útil muy corta, como papelería, material de limpieza o combustible.'),
(gen_random_uuid(), '¿Qué es el inventario de bienes de consumo?', 'Es el registro detallado de los bienes de consumo que la institución tiene en existencia, con información sobre su cantidad, tipo y ubicación.'),
(gen_random_uuid(), '¿Cuál es el procedimiento para la adquisición de bienes y servicios?', 'El procedimiento de adquisición se rige por la normativa nacional de contrataciones estatales, y se inicia con la solicitud de la unidad requeriente.'),
(gen_random_uuid(), '¿Qué es el SICOES?', 'El Sistema de Contrataciones Estatales (SICOES) es la plataforma web donde se publican los procesos de contratación de las entidades públicas en Bolivia.'),
(gen_random_uuid(), '¿Qué es una licitación pública?', 'Es el proceso de contratación en el que se invita a varias empresas a presentar propuestas para la adquisición de bienes o servicios de gran envergadura.'),
(gen_random_uuid(), '¿Qué es el proceso de inducción?', 'Es el proceso que se realiza para familiarizar a los nuevos funcionarios con la institución, su estructura, su cultura organizacional, sus normas y sus procedimientos.'),
(gen_random_uuid(), '¿Qué es un manual de funciones?', 'Es un documento que describe los objetivos, funciones, responsabilidades y requisitos de cada puesto de trabajo en la institución.'),
(gen_random_uuid(), '¿Cuál es el rol de la Dirección de Recursos Humanos?', 'La Dirección de Recursos Humanos es responsable de la gestión de personal, incluyendo la selección, contratación, evaluación, capacitación y el bienestar de los funcionarios.'),
(gen_random_uuid(), '¿Qué es un certificado de trabajo?', 'Es un documento que certifica que un funcionario ha trabajado en la institución, con información sobre el periodo de tiempo y el cargo desempeñado.');
